```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <map>
#include <sstream>

using namespace std;

int main() {
    // Get the filename from the user
    string filename;
    cout << "Enter the filename: ";
    cin >> filename;

    // Check if the file exists
    if (!exists(filename)) {
        cerr << "Error: File '" << filename << "' does not exist." << endl;
        return 1; // Indicate an error
    }

    // Create a map to store word counts
    map<string, int> wordCounts;

    // Open the file
    ifstream file(filename);
    if (!file.is_open()) {
        cerr << "Error opening file: " << filename << endl;
        return 1; // Indicate an error
    }

    // Read the file line by line
    string line;
    while (file >> line) {
        // Convert line to lowercase for case-insensitive counting
        string lowerLine = line;
        transform(lowerLine.begin(), lowerLine.end(), lowerLine.begin(), ::tolower);

        // Extract words from the line (handles spaces and punctuation)
        stringstream ss(lowerLine);
        string word;
        int count = 0;
        while (ss >> word) {
            wordCounts[word]++;
            count++;
        }

        //If the word was empty after processing this line, increment the count for the empty word.
        if (count == 0) {
            wordCounts[""]++;
        }

    }

    // Print the word counts
    cout << "Word counts:" << endl;
    for (auto const& [word, count] : wordCounts) {
        cout << word << ": " << count << endl;
    }

    return 0;
}
```

