### Full Code Example

Here’s a complete example of how to implement the game:

```c
#include <SDL2/SDL.h>
#include <SDL2/SDL_mixer.h>
#include <stdio.h>

// Initialize SDL
int main(int argc, char *argv[]) {
    SDL_init(SDL_INIT_VIDEO | SDL_INIT_AUDIO);
    if (SDL_load_file("snake.png") < 0) {
        return -1;
    }
    if (SDL_load_file("food.png") < 0) {
        return -1;
    }

    // Initialize SDL_mixer
    if (SDL_mixer_open(44100, MIX_DEFAULT_FORMAT, 2, 2048) < 0) {
        return -1;
    }

    // Create the game window
    SDL_Window *window = SDL_create_window("Snake Game",
                                            SDL_WINDOWPOS_CENTERED,
                                            SDL_WINDOWPOS_CENTERED,
                                            "Snake Game",
                                            SDL_WINDOW_SHOWN);
    if (!window) {
        return -1;
    }

    // Create a renderer
    SDL_Renderer *renderer = SDL_create_renderer(window, -1, SDL_RENDERER_ACCELERATED);
    if (!renderer) {
        return -1;
    }

    // Load snake and food images
    SDL_Image *snakeImage = SDL_load_image("snake.png");
    SDL_Image *foodImage = SDL_load_image("food.png");
    if (!snakeImage || !foodImage) {
        return -1;
    }

    // Initialize snake and food positions
    SDL_Rect snakeRect = {0, 0, 10, 10};
    SDL_Rect foodRect = {0, 0, 10, 10};

    // Initialize game variables
    bool gameOver = false;
    int score = 0;

    // Clock for controlling the frame rate
    SDL_Clock *clock = SDL_create_clock(100);

    // Font for displaying the score
    SDL_Font *font = SDL_font_create(NULL, 36);
    if (!font) {
        return -1;
    }

    // Event to handle user input
    SDL_Event event;
    while (!gameOver) {
        while (SDL_poll_event(SDL_EVENT_QUIT, &event)) {
            if (event.type == SDL_QUIT) {
                gameOver = true;
            }
        }

        // Move the snake
        if (SDL_key_get_pressed(SDL_KEY_UP)) {
            snakeRect.y -= 10;
        } else if (SDL_key_get_pressed(SDL_KEY_DOWN)) {
            snakeRect.y += 10;
        } else if (SDL_key_get_pressed(SDL_KEY_LEFT)) {
            snakeRect.x -= 10;
        } else if (SDL_key_get_pressed(SDL_KEY_RIGHT)) {
            snakeRect.x += 10;
        }

        // Move the food
        if (SDL_key_get_pressed(SDL_KEY_RIGHT)) {
            foodRect.x += 10;
        } else if (SDL_key_get_pressed(SDL_KEY_LEFT)) {
            foodRect.x -= 10;
        }

        // Check if the snake eats the food
        if (snakeRect.x == foodRect.x && snakeRect.y == foodRect.y) {
            score++;
            foodRect.x = (foodRect.x + 10) % SDL_RENDERER_WIDTH; // Move the food
        }

        // Check if the snake eats the wall
        if (snakeRect.x < 0 || snakeRect.x >= SDL_RENDERER_WIDTH ||
             snakeRect.y < 0 || snakeRect.y >= SDL_RENDERER_HEIGHT) {
            gameOver = true;
        }

        // Render the snake and food
        SDL_set_render_draw_color(renderer, SDL_RENDER_WHITE);
        SDL_rect(renderer, snakeRect);
        SDL_rect(renderer, foodRect);

        // Render the score
        SDL_set_font_color(font, SDL_FONT_WHITE);
        SDL_render_fill(renderer, 10, 10, "Score: %d", score);

        // Update the display
        SDL_render_update();
    }

    // Clean up
    SDL_destroy_window(window);
    SDL_destroy_renderer(renderer);
    SDL_destroy_font(font);
    SDL_quit();
    SDL_close();

    return 0;
}
```

