#include <iostream>
#include <fstream>
#include <string>
#include <vector>
#include <map>

int main() {
    // Define the word to search for
    std::string word = "example";

    // Initialize a map to store the count of occurrences of the word
    int count = 0;

    // Open the text file
    std::ifstream file("example.txt");

    // Read the file line by line
    if (file.is_open()) {
        std::string line;
        while (std::getline(file, line)) {
            // Convert the line to lowercase
            line = line;

            // Check if the word is in the line
            if (line.find(word) != std::string::npos) {
                // Increment the count
                count++;
            }
        }

        // Close the file
        file.close();
    } else {
        std::cout << "Unable to open file." << std::endl;
    }

    // Print the count
    std::cout << "Count: " << count << std::endl;

    return 0;
}
